mapper.insert:
INSERT INTO tb_users (user_uuid, user_id, user_password, user_email, user_role)
VALUES (#{userUuid, typeHandler=com.project.marathon.config.UUIDTypeHandler}
       , #{userId}, #{userPassword}, #{userEmail}, COALESCE(#{userRole}, 'USER'))

mapper.select:
UPDATE tb_users
SET user_last_login_dt = CURRENT_TIMESTAMP
WHERE user_id = #{userId}
RETURNING *;
